mapper.update:
UPDATE META.TB004 /* ColumnMeta.updateColumnMetaInfo */
SET 시스템수정자 = #{sysModifier}

, 컬럼명 = #{columnName}


, 컬럼카멜명 = #{columnCamelName}


, 컬럼스네이크명 = #{columnSnakeName}


, 컬럼타입 = #{columnType}


, 용어표준여부 = #{termStdYn}


, 용어일련번호 = #{termSno}

, 시스템수정일 = now()
WHERE 1 = 1
	AND 테이블메타일련번호 = #{tableMetaSno} :: INTEGER
	AND 컬럼메타일련번호 = #{columnMetaSno} :: INTEGER
